import React, { useEffect, useState } from 'react';
import './NotesList.css';

const NotesList = () => {
  const [notes, setNotes] = useState([]);
  const [selectedNote, setSelectedNote] = useState(null);

  useEffect(() => {
    fetch('https://api.example.com/notes')
      .then((response) => {
        if (response.ok) {
          return response.json();
        } else {
          throw new Error('Failed to fetch notes');
        }
      })
      .then((data) => setNotes(data))
      .catch((error) => console.error(error));
  }, []);

  const handleNoteClick = (note) => {
    setSelectedNote(note);
  };

  return (
    <div className="notes-list">
      <h2 className="title">Notes</h2>
      <div className="notes-container">
        {notes.map((note) => (
          <div key={note.id} className="note-card" onClick={() => handleNoteClick(note)}>
            <h3 className="note-title">Title: {note.title}</h3>
            <p className="note-content">Category: {note.category}</p>
            <p className="note-content">User: {note.user}</p>
            <p className="note-content">Notes: {note.notes}</p>
          </div>
        ))}
      </div>
      {selectedNote && (
        <div className="note-details">
          <h3 className="note-details-title">Title: {selectedNote.title}</h3>
          <p className="note-details-content">Category: {selectedNote.category}</p>
          <p className="note-details-content">User: {selectedNote.user}</p>
          <p className="note-details-content">Notes: {selectedNote.notes}</p>
        </div>
      )}
    </div>
  );
};

export default NotesList;
